import Header from "../components/header.vue";
import AppFooter from "../components/footer.vue";
import Loading from "../components/loading.vue"; // import axios from '../plugins/axios.js';

export default {
  name: 'app',
  components: {
    Header: Header,
    AppFooter: AppFooter,
    Loading: Loading
  },
  //路由观测,属性监听,watch监听
  watch: {
    $route: {
      handler: function handler(newValue) {
        var path = newValue.path;

        if (/home|follow|column/.test(path)) {
          this.$root.bNav = this.$root.bFoot = true;
        }

        if (/login|reg|detail/.test(path)) {
          this.$root.bNav = this.$root.bFoot = false;
        }

        if (path.includes('user')) {
          this.$root.bNav = false;
          this.$root.bFoot = true;
        }
      },
      immediate: true //首次运行

    }
  }
};
