import axios from '../plugins/axios.js';

const actions = {
  HOME: ({state,commit},payload) => {
    axios.get(`/api/home`,{params:{_limit:30}})
      .then(
        res=>commit('HOME',res.data.data)
      )
  },
  FOLLOW: ({state,commit},payload)=>{
    axios.get(`/api/follow`,{params:{_limit:20}})
      .then(
        res=>commit('FOLLOW',res.data.data)
      )
  },
  COLUMN: ({state,commit},payload)=>{
    axios.get(`/api/column`,{params:{_limit:12}})
      .then(
        res=>commit('COLUMN',res.data.data)
      )
  },
  BANNER: ({state,commit},payload)=>{
    axios.get(`/api/banner`,{params:{_limit:12}})
      .then(
        res=>commit('BANNER',res.data.data)
      )
  },
  DETAIL: ({state,commit},{_id,collectionName})=>{
    axios.get(`/api/${collectionName}/${_id}`)
      .then(
        res=>commit('DETAIL',res.data.data)
      )
  },
};
export default actions;
